import React, { useState } from 'react';
import './Skills.css';

const Skills = ({ skills }) => {
  const [viewMode, setViewMode] = useState('flowchart'); // 'flowchart', 'json', 'network'

  const skillCategories = [
    { 
      title: 'Frontend', 
      skills: skills.frontend, 
      icon: '🎨',
      color: '#764ba2',
      level: 'intermediate'
    },
    { 
      title: 'Backend', 
      skills: ['Node.js', 'Python', 'Django'], 
      icon: '⚙️',
      color: '#f093fb',
      level: 'basic'
    },
    { 
      title: 'Herramientas', 
      skills: skills.tools, 
      icon: '🛠️',
      color: '#4facfe',
      level: 'advanced'
    },
    { 
      title: 'Protocolos', 
      skills: skills.protocols, 
      icon: '🌐',
      color: '#43e97b',
      level: 'intermediate'
    },
    { 
      title: 'Metodologías', 
      skills: skills.methodologies, 
      icon: '📋',
      color: '#fa709a',
      level: 'advanced'
    },
    { 
      title: 'Idiomas', 
      skills: skills.languages, 
      icon: '🌍',
      color: '#ffecd2',
      level: 'expert'
    }
  ];

  const getSkillLevel = (level) => {
    const levels = {
      'expert': { stars: '⭐⭐⭐', percentage: 95, label: 'Experto' },
      'advanced': { stars: '⭐⭐', percentage: 80, label: 'Avanzado' },
      'intermediate': { stars: '⭐', percentage: 65, label: 'Intermedio' },
      'basic': { stars: '🔰', percentage: 40, label: 'Básico' }
    };
    return levels[level] || levels['intermediate'];
  };

  const renderFlowchart = () => (
    <div className="flowchart-container">  
      {skillCategories.map((category, index) => {
        return (
          <div 
            key={index}
            className="flowchart-node category-node"
            style={{ 
              '--color': category.color
            }}
          >
            <div className="node-content">
              <span className="node-icon">{category.icon}</span>
              <span className="node-title">{category.title}</span>
            </div>
            
            <div className="node-connections">
              {category.skills.map((skill, skillIndex) => (
                <div 
                  key={skillIndex}
                  className="skill-connection"
                >
                  <div className="connection-dot"></div>
                  <span className="skill-name">{skill}</span>
                </div>
              ))}
            </div>
          </div>
        );
      })}
    </div>
  );

  const renderJsonViewer = () => (
    <div className="json-viewer">
      <div className="json-container">
        <div className="json-header">
          <span className="json-icon">📄</span>
          <span className="json-title">skills.json</span>
          <div className="json-status">
            <div className="status-dot"></div>
            <span>Live Data</span>
          </div>
        </div>
        
        <div className="json-content">
          <pre className="json-code">
{`{
  "developer": {
    "name": "Yusjuani Hernández",
    "role": "Frontend Developer / UX UI Designer",
    "location": "Santiago, Chile",
    "skills": {`}
          </pre>
          
          {skillCategories.map((category, index) => (
            <div key={index} className="json-category">
              <pre className="json-code">
{`    "${category.title.toLowerCase().replace(/\s+/g, '_')}": {
      "level": "${getSkillLevel(category.level).label}",
      "proficiency": ${getSkillLevel(category.level).percentage}%,
      "technologies": [`}
              </pre>
              
              <div className="json-skills">
                {category.skills.map((skill, skillIndex) => (
                  <div 
                    key={skillIndex}
                    className="json-skill"
                  >
                    <pre className="json-code">
{`        "${skill}"${skillIndex < category.skills.length - 1 ? ',' : ''}`}
                    </pre>
                  </div>
                ))}
              </div>
              
              <pre className="json-code">
{`      ],
      "icon": "${category.icon}",
      "color": "${category.color}"
    }${index < skillCategories.length - 1 ? ',' : ''}`}
              </pre>
            </div>
          ))}
          
          <pre className="json-code">
{`  },
  "last_updated": "${new Date().toISOString().split('T')[0]}",
  "status": "active"
}`}
          </pre>
        </div>
      </div>
    </div>
  );

  const renderNetwork = () => (
    <div className="network-container">
      <div className="network-center">
        <div className="center-node">
          <span className="center-icon">👩‍💻</span>
          <span className="center-title">Yusjuani</span>
        </div>
      </div>
      
      {skillCategories.map((category, index) => (
        <div 
          key={index}
          className="network-node"
          style={{ 
            '--angle': `${(index * 360) / skillCategories.length}deg`,
            '--color': category.color
          }}
        >
          <div className="node-content">
            <span className="node-icon">{category.icon}</span>
            <span className="node-title">{category.title}</span>
            <div className="node-skills">
              {category.skills.slice(0, 2).map((skill, skillIndex) => (
                <span key={skillIndex} className="skill-tag">{skill}</span>
              ))}
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  return (
    <section className="skills">
      <div className="container">
        <h2 className="section-title">
          <span className="title-text">Habilidades Técnicas</span>
          <div className="title-underline"></div>
        </h2>
        
        <div className="view-controls">
          <button 
            className={`view-btn ${viewMode === 'flowchart' ? 'active' : ''}`}
            onClick={() => setViewMode('flowchart')}
          >
            <span className="btn-icon">📋</span>
            <span className="btn-text">Habilidades por Categoría</span>
          </button>
          <button 
            className={`view-btn ${viewMode === 'json' ? 'active' : ''}`}
            onClick={() => setViewMode('json')}
          >
            <span className="btn-icon">📄</span>
            <span className="btn-text">JSON Viewer</span>
          </button>
          <button 
            className={`view-btn ${viewMode === 'network' ? 'active' : ''}`}
            onClick={() => setViewMode('network')}
          >
            <span className="btn-icon">🕸️</span>
            <span className="btn-text">Red de Habilidades</span>
          </button>
        </div>
        
        <div className="skills-visualization">
          {viewMode === 'flowchart' && renderFlowchart()}
          {viewMode === 'json' && renderJsonViewer()}
          {viewMode === 'network' && renderNetwork()}
        </div>
        
      </div>
    </section>
  );
};

export default Skills;
