import React, { useState, useEffect } from 'react';
import './Skills.css';

const Skills = ({ skills }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(true), 500);
    return () => clearTimeout(timer);
  }, []);

  const skillCategories = [
    { 
      title: 'Lenguajes de Programación', 
      skills: skills.programming, 
      icon: '💻',
      color: '#667eea'
    },
    { 
      title: 'Frontend', 
      skills: skills.frontend, 
      icon: '🎨',
      color: '#764ba2'
    },
    { 
      title: 'Backend', 
      skills: skills.backend, 
      icon: '⚙️',
      color: '#f093fb'
    },
    { 
      title: 'Herramientas', 
      skills: skills.tools, 
      icon: '🛠️',
      color: '#4facfe'
    },
    { 
      title: 'Protocolos', 
      skills: skills.protocols, 
      icon: '🌐',
      color: '#43e97b'
    },
    { 
      title: 'Metodologías', 
      skills: skills.methodologies, 
      icon: '📋',
      color: '#fa709a'
    },
    { 
      title: 'Idiomas', 
      skills: skills.languages, 
      icon: '🌍',
      color: '#ffecd2'
    }
  ];

  return (
    <section className="skills">
      <div className="container">
        <h2 className="section-title">
          <span className="title-text">Habilidades Técnicas</span>
          <div className="title-underline"></div>
        </h2>
        
        <div className="skills-grid">
          {skillCategories.map((category, index) => (
            <div 
              key={index} 
              className={`skill-category ${isVisible ? 'animate-in' : ''}`}
              style={{ '--delay': `${index * 0.1}s` }}
            >
              <div className="category-header">
                <div className="category-icon-wrapper" style={{ '--color': category.color }}>
                  <span className="category-icon">{category.icon}</span>
                </div>
                <h3 className="category-title">{category.title}</h3>
              </div>
              
              <div className="skills-container">
                {category.skills.map((skill, skillIndex) => (
                  <div 
                    key={skillIndex} 
                    className="skill-item"
                    style={{ '--skill-delay': `${skillIndex * 0.05}s` }}
                  >
                    <div className="skill-content">
                      <span className="skill-name">{skill}</span>
                      <div className="skill-level">
                        <div 
                          className="skill-bar"
                          style={{ 
                            '--skill-color': category.color,
                            '--skill-width': `${Math.min(85 + Math.random() * 15, 100)}%`
                          }}
                        ></div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
